sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},
	isDatePast:function(strDate){
		var today = new GlideDateTime();
		var inputdate = new GlideDateTime(strDate);
		if(inputdate.before(today)){
			return true;
		}else{
			return false;
		}
	},
	isSameDate:function(strDate){
		var today = new GlideDateTime();
		var inputdate = new GlideDateTime(strDate);
		if(inputdate.getDate()==today.getDate()){
			return true;
		}else{
			return false;
		}
	},
	type: 'NeedItUtils'
};
